SELECT value.ITEMID as "itemId", value.TAPNO as "tapNo", VALUE(value.VALUE,' ') as "value", item.KEYNAME as "keyName", item.REMARK as "remark" FROM iplature.T_TS_TAP_VALUE AS value, iplature.T_TS_TAP_ITEM AS item WHERE value.ITEMID = item.ITEMID AND value.ITEMID IN (2815,2820,2096,2825,2822,2824,2831,2832) AND value.TAPNO = ( select TAPNO from IPLATURE.T_TS_TAP where ENDTIME is not null
and TAPNO = #{tapNo}
order by TAPNO desc fetch first 1 rows only )

