SELECT ${fieldStr} FROM IPLATURE.${tableName} WHERE RESULTID IN ( SELECT RESULTID FROM IPLATURE.T_CUTOFF_RESULT WHERE CUTOFFID = 1
AND RESULTID = #{resultId}
order by resultId desc fetch first ${rows} rows only )

	    SELECT
	    	${fieldStr}
	    FROM
	    	IPLATURE.${tableName}
	    WHERE
	    	RESULTID IN


	    	(
			   SELECT RESULTID FROM IPLATURE.T_CUTOFF_RESULT
			   WHERE CUTOFFID = 1

			   		and RESULTID = #{resultId}


    				and CLOCK < TIMESTAMP(#{time}, 'YYYY-MM-DD-HH24:MI:SS.US')

			   order by resultId desc fetch first ${rows} rows only
	  		)



	       		${HeatMapResultIdStr}

