SELECT t.POINTID, t.ITEMID, t.GROUPID, t1.POINTTYPE, t2.STARTDATE, t1.Z AS "height" , t.val AS "radius", ROUND ( ( SELECT DISTINCT angle FROM IPLATURE.t_md_er_couple a, IPLATURE.t_md_er_couple_point b WHERE a.coupleid = b.coupleid AND b.pointid = t1.pointid ), 0 ) as "angle" FROM IPLATURE.t_md_er_value_wall_point t, IPLATURE.t_md_er_point t1, IPLATURE.t_md_er_value_group t2 WHERE t.POINTID = t1.POINTID AND t1.Z = #{high} AND t.ITEMID = #{itemId} AND t1.POINTTYPE = 6 AND t.GROUPID = t2.GROUPID
AND t2.STARTDATE = #{startTime}
AND t2.STARTDATE = (select max(STARTDATE) from IPLATURE.T_MD_ER_VALUE_GROUP)
